fn get_number(n: u32) -> String {
    if n == 0 {
        return ZERO.to_string();
    }
    std::iter::repeat(ONE).take(n as usize).join(" + ")
}

fn get_string(s: String) -> String {
    s.chars()
        .map(|t| -> String {
            match t {
                'a' => A.to_string(),
                'b' => B.to_string(),
                'o' => O.to_string(),
                'e' => E.to_string(),
                'c' => LOWER_C.to_string(),
                't' => T.to_string(),
                ' ' => SPACE.to_string(),
                'f' => F.to_string(),
                's' => LOWER_S.to_string(),
                'r' => R.to_string(),
                'u' => U.to_string(),
                'i' => I.to_string(),
                'n' => N.to_string(),
                'S' => UPPER_S.to_string(),
                'g' => G.to_string(),
                'p' => P.to_string(),
                '\\' => BACK_SLASH.to_string(),
                'd' => D.to_string(),
                'h' => H.to_string(),
                'm' => M.to_string(),
                'C' => UPPER_C.to_string(),
                _ => {
                    let char_code = t as u32;
                    let constructed = format!(
                        "([]+[])[{}][{}]({})",
                        get_string("constructor".to_string()),
                        get_string("fromCharCode".to_string()),
                        get_number(char_code)
                    );
                    constructed
                }
            }
        })
        .join("+")
}

pub fn compile(code: String) -> String {
    format!(
        "(()=>{{}})[{}]({})()",
        get_string("constructor".to_string()),
        get_string(code)
    )
}
